import { HttpContextContract } from '@ioc:Adonis/Core/HttpContext'
import Arquivo from 'App/Models/Arquivo'
import Drive from '@ioc:Adonis/Core/Drive'
import { v4 as uuid } from 'uuid'
import { Response } from 'aws-sdk'
import { schema } from '@ioc:Adonis/Core/Validator'
import pdf from 'html-pdf'

export default class ArquivosController {

  public async indexByChave({ request, response }: HttpContextContract) {
    try {
      const chave = request.param('chave')
      const arquivo = await Arquivo.findByOrFail('chave', chave)
      const urlRes = await Drive.get(arquivo.chave)
      return urlRes
    } catch (error) {
      return 'Falha ao pegar o arquivo!'
    }
  }

  public async store({ request }: HttpContextContract) {
    const image = request.input('file')

    const tipo_conteudo = "text"
    const ACL = 'public-read'
    const nome = "minionss"
    const chave = `${(Math.random() * 100).toString()}-${nome}`


    await Drive.put(chave, image, {
      contentType: tipo_conteudo,
      visibility: ACL,
    })


    await Arquivo.create({
      nome,
      chave,
      tipo_conteudo,
    })


    return chave;
  }


  public async storePdf({ request }: HttpContextContract) {

    const nomePaciente = request.input('nome');
    const dataNascimento = request.input('data');
    const tituloReceita = request.input('titulo');
    const descricao = request.input('descricao');

    if (!nomePaciente || !dataNascimento || !tituloReceita) {
      return 0;
    }
    const conteudoPdf = `
      <img src="data:image/png;base64,"/>
      <hr>
      <h1>NOME: ${nomePaciente}</h1>
      <h2>DATA NASC: ${dataNascimento} </h2>
      <h1> ${tituloReceita} </h1>
      <p> Descrição: ${descricao} </p>
      <hr>
      <img src="data:image/png;base64,"/>
    `

    const tipo_conteudo = "pdf"
    const ACL = 'public-read'
    const nome = "PDF"
    const chave = `${(Math.random() * 100).toString()}-${nome}`
    await pdf.create(conteudoPdf, {}).toStream((err, res) => {
      if (err) {
        console.log(err);
      }
      else {
        Drive.putStream(chave, res, {
          contentType: tipo_conteudo,
          visibility: ACL,
        });

        Arquivo.create({
          nome,
          chave,
          tipo_conteudo,
        });

        return chave;

      }
    });

  }


  public async update({ }: HttpContextContract) { }

  public async destroy({ request }: HttpContextContract) {
    try {
      const chave = request.param('chave')
      const arquivo = await Arquivo.findByOrFail('chave', chave)

      await Drive.delete(arquivo.chave)
      await arquivo.delete()

      return 'Arquivo deletado com sucesso!'
    } catch (error) {
      return 'Falha ao apagar o arquivo!'
    }
  }
}
